INSERT INTO libros (Nombre, Genero, Autor, Editorial, Descripcion, EnPrestamo, Activo) VALUES ('Cien años de soledad', 'Realismo mágico', 'Gabriel García Márquez', 'Editorial Sudamericana', 'Cien años de soledad es una novela del escritor colombiano Gabriel García Márquez. La historia sigue a la familia Buendía en el ficticio pueblo de Macondo, a lo largo de varias generaciones. La obra combina elementos de realismo y fantasía, y ha sido aclamada como una de las obras maestras de la literatura latinoamericana.', 1, 1);
INSERT INTO libros (Nombre, Genero, Autor, Editorial, Descripcion, EnPrestamo, Activo) VALUES ('1984', 'Ciencia ficción', 'George Orwell', 'Editorial Secker & Warburg', '1984 es una novela distópica escrita por George Orwell. La historia se desarrolla en un estado totalitario donde el gobierno controla todos los aspectos de la vida de los ciudadanos. El libro aborda temas como la vigilancia, la manipulación de la información y la pérdida de libertad individual.', 1, 1);
INSERT INTO libros (Nombre, Genero, Autor, Editorial, Descripcion, EnPrestamo, Activo) VALUES ('Orgullo y prejuicio', 'Novela romántica', 'Jane Austen', 'Editorial Thomas Egerton', 'Orgullo y prejuicio es una novela de Jane Austen que sigue la historia de Elizabeth Bennet y su relación con el orgulloso Sr. Darcy. La obra aborda temas como el matrimonio, la clase social y los prejuicios de la época. Es considerada una de las obras más destacadas de la literatura inglesa.', 1, 1);
INSERT INTO libros (Nombre, Genero, Autor, Editorial, Descripcion, EnPrestamo, Activo) VALUES ('El gran Gatsby', 'Ficción', 'F. Scott Fitzgerald', 'Editorial Charles Scribner\s Sons', 'El gran Gatsby es una novela escrita por F. Scott Fitzgerald que retrata la vida de Jay Gatsby, un millonario en la década de 1920. La historia aborda temas como la riqueza, el amor no correspondido y la decadencia de la sociedad estadounidense en esa época.', 1, 1);
INSERT INTO libros (Nombre, Genero, Autor, Editorial, Descripcion, EnPrestamo, Activo) VALUES ('Don Quijote de la Mancha', 'Novela', 'Miguel de Cervantes Saavedra', 'Editorial Francisco de Robles', 'Don Quijote de la Mancha es una novela escrita por Miguel de Cervantes Saavedra. Narra las aventuras de un caballero llamado Don Quijote que se embarca en múltiples hazañas en nombre de la justicia y el amor. Es considerada una de las obras más importantes de la literatura universal.', 0, 1);
INSERT INTO libros (Nombre, Genero, Autor, Editorial, Descripcion, EnPrestamo, Activo) VALUES ('Matar a un ruiseñor', 'Novela', 'Harper Lee', 'Editorial J. B. Lippincott & Co.', 'Matar a un ruiseñor es una novela escrita por Harper Lee. La historia se centra en Scout Finch, una niña que narra la injusticia racial en un pequeño pueblo del sur de Estados Unidos durante la década de 1930. El libro aborda temas como la tolerancia, la inocencia y la lucha por la igualdad.', 0, 1);
INSERT INTO libros (Nombre, Genero, Autor, Editorial, Descripcion, EnPrestamo, Activo) VALUES ('Crónica de una muerte anunciada', 'Novela', 'Gabriel García Márquez', 'Editorial La Oveja Negra', 'Crónica de una muerte anunciada es una novela escrita por Gabriel García Márquez. La historia gira en torno al asesinato de Santiago Nasar, un joven en un pequeño pueblo colombiano. A través de múltiples perspectivas, la novela explora los temas de la fatalidad, el honor y la responsabilidad colectiva.', 0, 1);
INSERT INTO libros (Nombre, Genero, Autor, Editorial, Descripcion, EnPrestamo, Activo) VALUES ('Fahrenheit 451', 'Ciencia ficción', 'Ray Bradbury', 'Editorial Ballantine Books', 'Fahrenheit 451 es una novela distópica escrita por Ray Bradbury. La historia se desarrolla en una sociedad futurista en la que los libros están prohibidos y son quemados por los bomberos. El libro aborda temas como la censura, la alienación y la importancia de la lectura.', 0, 1);
INSERT INTO libros (Nombre, Genero, Autor, Editorial, Descripcion, EnPrestamo, Activo) VALUES ('El guardián entre el centeno', 'Novela', 'J. D. Salinger', 'Editorial Little, Brown and Company', 'El guardián entre el centeno es una novela escrita por J. D. Salinger. La historia sigue a Holden Caulfield, un adolescente que se enfrenta a la transición a la edad adulta. El libro aborda temas como la alienación, la identidad y la crítica a la sociedad.', 0, 1);
INSERT INTO libros (Nombre, Genero, Autor, Editorial, Descripcion, EnPrestamo, Activo) VALUES ('Los juegos del hambre', 'Ciencia ficción', 'Suzanne Collins', 'Editorial Scholastic Press', 'Los juegos del hambre es la primera novela de la trilogía escrita por Suzanne Collins. Ambientada en un futuro distópico, sigue la historia de Katniss Everdeen mientras lucha por sobrevivir en los mortales juegos organizados por el Capitolio. El libro aborda temas como la opresión, la resistencia y la ética en la sociedad.', 0, 1);
INSERT INTO libros (Nombre, Genero, Autor, Editorial, Descripcion, EnPrestamo, Activo) VALUES ('El alquimista', 'Novela', 'Paulo Coelho', 'Editorial Planeta', 'El alquimista es una novela escrita por Paulo Coelho. La historia sigue a Santiago, un joven pastor que emprende un viaje en busca de su tesoro personal. A lo largo del camino, descubre lecciones sobre el destino, la intuición y el significado de la vida. El libro ha sido elogiado por su mensaje espiritual y motivacional.', 0, 1);
INSERT INTO libros (Nombre, Genero, Autor, Editorial, Descripcion, EnPrestamo, Activo) VALUES ('El señor de los anillos', 'Fantasía', 'J.R.R. Tolkien', 'Editorial George Allen & Unwin', 'El señor de los anillos es una trilogía de novelas épicas escritas por J.R.R. Tolkien. La historia se desarrolla en la Tierra Media y sigue la lucha entre el bien y el mal mientras un grupo de personajes emprende una misión para destruir un poderoso anillo. El libro es conocido por su rica mitología y su estilo narrativo.', 0, 1);
INSERT INTO libros (Nombre, Genero, Autor, Editorial, Descripcion, EnPrestamo, Activo) VALUES ('Crimen y castigo', 'Novela', 'Fyodor Dostoyevsky', 'Editorial The Russian Messenger', 'Crimen y castigo es una novela escrita por Fyodor Dostoyevsky. La historia sigue a Raskólnikov, un estudiante que comete un asesinato y debe enfrentar las consecuencias psicológicas y morales de sus acciones. El libro explora temas como la culpa, el castigo y la redención.', 0, 1);
INSERT INTO libros (Nombre, Genero, Autor, Editorial, Descripcion, EnPrestamo, Activo) VALUES ('El retrato de Dorian Gray', 'Novela', 'Oscar Wilde', 'Editorial Ward, Lock and Company', 'El retrato de Dorian Gray es una novela escrita por Oscar Wilde. Cuenta la historia de Dorian Gray, un joven cuya belleza se conserva mientras un retrato suyo envejece en su lugar. La novela aborda temas como la belleza, la vanidad y la corrupción moral.', 0, 1);
INSERT INTO libros (Nombre, Genero, Autor, Editorial, Descripcion, EnPrestamo, Activo) VALUES ('Drácula', 'Novela gótica', 'Bram Stoker', 'Editorial Archibald Constable and Company', 'Drácula es una novela gótica escrita por Bram Stoker. Narra la historia del conde Drácula, un vampiro que busca expandir su dominio sobre la humanidad. La novela ha sido influyente en la representación del género vampírico y explora temas como el bien y el mal, la sexualidad y la inmortalidad.', 0, 1);
INSERT INTO libros (Nombre, Genero, Autor, Editorial, Descripcion, EnPrestamo, Activo) VALUES ('El nombre del viento', 'Fantasía', 'Patrick Rothfuss', 'Editorial DAW Books', 'El nombre del viento es la primera novela de la trilogía "Crónica del asesino de reyes" escrita por Patrick Rothfuss. Sigue la historia de Kvothe, un talentoso músico y mago en busca de venganza. El libro ha sido elogiado por su prosa poética y su detallada construcción del mundo.', 0, 1);